import React, { useRef, useState } from "react";

const SimpleInput = (props) => {
  const [nameInputIsTouched, setNameInputIsTouched] = useState(false);
  const [emailInputIsTouched, setEmailInputIsTouched] = useState(false);
  let formIsValid = false;

  const [inputEl, setInputEl] = useState("");
  const [inputEmail, setInputEmail] = useState('')

  const i = useRef(null);

  const enteredNameIsValid = inputEl.trim() !== "";
  const enteredEmailIsValid = inputEmail.trim() !== '';

  const nameInputIsInvalid = !enteredNameIsValid && nameInputIsTouched;
  const emailInputIsInvalid = !enteredEmailIsValid && emailInputIsTouched;


  const inputHandler = (e) => {
    setInputEl(i.current.value);
    setNameInputIsTouched(true);
  };

  const inputEmailHandler = (e) => {
    setInputEmail(e.target.value);
    setEmailInputIsTouched(true);
  }


  const nameInputBlurHandler = () => {
    setNameInputIsTouched(true);
  };

  const emailInputBlurHandler = () => {
    setEmailInputIsTouched(true);
    console.log(emailInputIsTouched, emailInputIsInvalid)
  }

  const formSubmittionHandler = (e) => {
    e.preventDefault();
    setNameInputIsTouched(true);
    setEmailInputIsTouched(true);

    console.log(formIsValid)
    if (inputEl.trim() === "") {
      return;
    }

    console.log("BLA BLA");
    setInputEl("");
    setNameInputIsTouched(false);
    setEmailInputIsTouched(false);
  };

  const nameInputClasses = !nameInputIsInvalid
    ? "form-control"
    : "form-control invalid";

  const emailInputClasses = !emailInputIsInvalid
    ? "form-control"
    : "form-control invalid";

  formIsValid = (!nameInputIsInvalid && !emailInputIsInvalid)
  return (
    <form onSubmit={formSubmittionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          ref={i}
          onChange={inputHandler}
          onBlur={nameInputBlurHandler}
          value={inputEl}
        />
      </div>
      {!enteredNameIsValid && nameInputIsTouched && <p>"Name must not be empty"</p>}

      <div className={emailInputClasses}>
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={inputEmailHandler}
          onBlur={emailInputBlurHandler}
          value={inputEmail}
        />
      </div>
      {!enteredEmailIsValid && emailInputIsTouched && <p>"Name must not be empty"</p>}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
      <div>{inputEl}</div>
    </form>
  );
};

export default SimpleInput;
