import React, { useRef, useEffect, useState } from "react";

const SimpleInput = (props) => {
  // const [enteredNameValid, setEnteredNameValid] = useState(false);
  const [inputEl, setInputEl] = useState("");
  const i = useRef(null);

  const enteredNameIsValid = inputEl.trim() !== ''

  const inputHandler = (e) => {
    setInputEl(i.current.value);
    if (i.current.value.length > 0) {
      setEnteredNameValid(true);

    }
  };

  const nameInputBlurHandler = () => {
    if (inputEl.length < 1) setEnteredNameValid(false);
  };

  const formSubmittionHandler = (e) => {
    e.preventDefault();

    if (inputEl.trim() === "") {
      setEnteredNameValid(false);
      return;
    }
    setEnteredNameValid(true);
    console.log("BLA BLA");
    setInputEl("");
  };

  const nameInputClasses = enteredNameValid
    ? "form-control"
    : "form-control invalid";
  return (
    <form onSubmit={formSubmittionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          ref={i}
          onChange={inputHandler}
          onBlur={nameInputBlurHandler}
          value={inputEl}
        />
      </div>
      {!enteredNameIsValid && <p>"Name must not be empty"</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
      <div>{inputEl}</div>
    </form>
  );
};

export default SimpleInput;
