import React, { useRef, useEffect, useState } from "react";

const SimpleInput = (props) => {
  const [formIsTouched, setFormIsTouched] = useState(false);
  const [inputEl, setInputEl] = useState("");
  const i = useRef(null);
  const [inputEmail, setInputEmail] = useState('')

